import { useState } from "react";
import styled from "styled-components";
import Container from "react-bootstrap/Container";
import Nav from "react-bootstrap/Nav";
import Navbar from "react-bootstrap/Navbar";
import Dropdown from "react-bootstrap/Dropdown";
import { NavLink } from "react-router-dom";
import StyledButton from "./StyledButton";
import Footer from "./Footer";

const StyledNavbar = styled(Navbar)`
  background-color: #89abe3;
  height: 5rem;
`;

const StyledBrand = styled(NavLink)`
  display: flex;
  align-items: center;
  text-decoration: none;
  font-size: 2rem;
  color: white;
  padding: 1rem;

  margin-right: 5rem;

  &:hover {
    color: #edcac4;
  }
`;

const StyledLink = styled(NavLink)`
  text-decoration: none;
  font-size: 1.4rem;
  color: white;
  margin-right: 2rem;
  padding-top: 1rem;
  padding-bottom: 1rem;
  display: flex;
  align-items: center;
  height: 6rem;

  &:hover {
    color: #edcac4;
  }

  &.active {
    color: #edcac4;
  }
`;

const StyledDropDown = styled(Dropdown.Toggle)`
  background-color: white;
  color: #89abe3;
  border-radius: 5px;
  border-color: white;

  &:hover {
    color: white;
    background-color: #edcac4;
    border-color: #edcac4;
  }

  &:active {
    color: white;
    background-color: #edcac4;
    border-color: #edcac4;
  }
`;

function Panel(props) {
  const [username] = useState("");

  return (
    <>
      <StyledNavbar>
        <Container>
          <StyledBrand to="/">Online Store</StyledBrand>

          <Nav className="me-auto">
            <StyledLink to="/for-him">Gifts For Him</StyledLink>
            <StyledLink to="/for-her">Gifts For Her</StyledLink>
            <StyledLink to="/under-10">Budget Gifts</StyledLink>
          </Nav>

          {username === "" ? (
            <StyledButton
              variant="secondary"
              text="Login / Register"
            ></StyledButton>
          ) : (
            <Dropdown>
              <StyledDropDown id="dropdown-basic" variant="secondary">
                {username}
              </StyledDropDown>

              <Dropdown.Menu>
                <Dropdown.Item href="#/action-1">Cart</Dropdown.Item>
                <Dropdown.Item href="#/action-2">Settings</Dropdown.Item>
                <Dropdown.Divider />
                <Dropdown.Item href="#/action-3">Logout</Dropdown.Item>
              </Dropdown.Menu>
            </Dropdown>
          )}
        </Container>
      </StyledNavbar>
      {props.children}
      <Footer />
    </>
  );
}

export default Panel;
