import Carousel from "react-bootstrap/Carousel";
import { Image } from "react-bootstrap";
import Spinner from "react-bootstrap/Spinner";
import styled from "styled-components";
import axios from "axios";
import { useState, useEffect } from "react";

const Item = styled.div`
  margin-top: 3rem;
  width: 1800px;
  height: 400px;
  background-color: #89abe3;
  display: flex;
  justify-content: center;
  align-items: center;
  color: white;
  font-size: 24px;
  font-weight: bold;
  text-align: center;
  border-radius: 50px;
`;

const StyledDiv = styled.div`
  width: 100vw;
  height: 100vh;
  background-color: #edcac4;
`;
const Frame = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 100%;
`;

function HomePage() {
  const server = "http://localhost:3000/items";
  const [featured, setFeatured] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  useEffect(() => {
    axios
      .get(server)
      .then((res) => {
        const picked3 = [];
        const data = res.data.items;
        data.sort(() => Math.random() - 0.5);
        for (let i = 0; i < 3; i++) {
          picked3.push(data.pop());
        }
        setFeatured(picked3);
        console.log(picked3);
      })
      .catch((err) => {
        console.error("Axios error:", err);
      });
  }, []);

  if (isLoading) {
    return (
      <StyledDiv>
        <Spinner animation="border" role="status">
          <span className="visually-hidden">Loading...</span>
        </Spinner>
      </StyledDiv>
    );
  }

  return (
    <StyledDiv>
      <Carousel>
        <Carousel.Item>
          <Frame>
            <Image src={featured[0].image} alt={featured[0].title} />
          </Frame>
          <Carousel.Caption>{featured[0].description}</Carousel.Caption>
        </Carousel.Item>
        <Carousel.Item>
          <Frame>
            <Image src={featured[1].image} alt={featured[1].title} />
          </Frame>
          <Carousel.Caption>{featured[1].description}</Carousel.Caption>
        </Carousel.Item>
        <Carousel.Item>
          <Frame>
            <Image src={featured[2].image} alt={featured[2].title} />
          </Frame>
          <Carousel.Caption>{featured[2].description}</Carousel.Caption>
        </Carousel.Item>
      </Carousel>
    </StyledDiv>
  );
}

export default HomePage;
