import { useState, useEffect } from "react";
import styled from "styled-components";
import axios from "axios";
import StyledButton from "./StyledButton";
import Loader from "./Loader";
import { NavLink } from "react-router-dom";
import Card from "react-bootstrap/Card";
import Form from "react-bootstrap/Form";
import CloseButton from "react-bootstrap/CloseButton";

const StyledDiv = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  padding-bottom: 2rem;
`;

const Bar = styled.div`
  width: 80%;
  outline: 2px solid white;
  display: flex;
  align-items: center;
  justify-content: center;
  background-color: #89abe3;
  padding: 1rem;
  border-radius: 10px;
`;

const Col = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
`;

const Row = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
`;

const SearchDiv = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  flex-direction: row;
  margin-bottom: 2rem;
  margin-top: 1rem;
  margin-left: 3rem;
  margin-right: 3rem;
  border-radius: 10px;
  padding: 1rem;
  outline: 2px solid white;
`;

const StyledForm = styled(Form)`
  display: flex;
  align-items: center;
  justify-content: flex-start;
  flex-direction: row;
  margin-right: 1rem;
`;

const SearchBar = styled(Form.Control)`
  color: #89abe3;
  width: 100%;

  &:focus {
    color: #89abe3;
  }

  &::placeholder {
    color: #89abe3;
  }
`;

const Close = styled(CloseButton)`
  margin-left: -2rem;
`;

const ItemsDiv = styled.div`
  display: flex;
  flex-wrap: wrap;
  align-items: center;
  justify-content: space-evenly;
`;

const CustomCard = styled(Card)`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: flex-end;
  width: 20rem;
  height: 30rem;
  margin-bottom: 2rem;
  padding-bottom: 2rem;
`;

const StyledNavLink = styled(NavLink)`
  text-decoration: none;
`;

const StyledImage = styled(Card.Img)`
  object-fit: contain;
  max-height: 18rem;
  padding: 2rem;
`;

const Body = styled(Card.Body)`
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const Title = styled(Card.Title)`
  text-align: center;
  color: #89abe3;
`;

const Text = styled(Card.Text)`
  color: #89abe3;
`;

function Catalog() {
  const server = "http://localhost:3000/items";
  const [query, setQuery] = useState("");
  const [lastQuery, setLastQuery] = useState("");
  const [items, setItems] = useState([]);
  const [filtered, setFiltered] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  const handleChange = (e) => {
    setQuery(e.target.value);
  };

  const handleClear = (e) => {
    if (query !== "") {
      setQuery("");
    }
  };

  const handleSearch = () => {
    if (lastQuery !== query) {
      console.log("filtered");
      const newFiltered = items.filter((item) =>
        item.title.toLowerCase().includes(query)
      );
      setLastQuery(query);
      setFiltered(newFiltered);
    } else {
      console.log("didnt filter");
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    handleSearch();
  };

  useEffect(() => {
    axios
      .get(server)
      .then((res) => {
        setItems(res.data.items);
        setFiltered(res.data.items);
      })
      .catch((err) => {
        console.error(err);
      });
  }, []);

  return (
    <StyledDiv>
      <Bar>
        <Col>
          <SearchDiv>
            <StyledForm onSubmit={handleSubmit}>
              <SearchBar
                placeholder="Search For Item"
                onChange={handleChange}
                value={query}
              />
              <Close onClick={handleClear} />
            </StyledForm>
            <StyledButton
              variant="secondary"
              text="Search"
              onClick={handleSearch}
            />
          </SearchDiv>
          {isLoading ? (
            <Loader />
          ) : (
            <ItemsDiv>
              {filtered.map((item) => {
                return (
                  <CustomCard key={item.id}>
                    <StyledNavLink to={"/" + item.id}>
                      <StyledImage variant="top" src={item.image} />
                      <Body>
                        <Title>{item.title}</Title>
                        <Text>${item.price.toFixed(2)}</Text>
                      </Body>
                    </StyledNavLink>
                    <StyledButton variant="secondary" text="Add to cart" />
                  </CustomCard>
                );
              })}
            </ItemsDiv>
          )}
        </Col>
      </Bar>
    </StyledDiv>
  );
}

export default Catalog;
