import { useState } from "react";
import styled, { css } from "styled-components";
import Container from "react-bootstrap/Container";
import Nav from "react-bootstrap/Nav";
import Navbar from "react-bootstrap/Navbar";
import Button from "react-bootstrap/Button";
import Dropdown from "react-bootstrap/Dropdown";
import DropdownButton from "react-bootstrap/DropdownButton";

function Header() {
  const [username, setUsername] = useState("Susna");

  const StyledNavbar = styled(Navbar)`
    background-color: #89abe3;
    height: 5rem;
  `;

  const StyledButton = styled(Button)`
    background-color: white;
    color: #89abe3;
    border-color: white;

    &:hover {
      background-color: #edcac4;
      border-color: #edcac4;
    }

    &:active {
      background-color: #edcac4;
    }
  `;

  const StyledBrand = styled(Navbar.Brand)`
    font-size: 2rem;
    color: white;

    margin-right: 5rem;

    &:hover {
      color: #edcac4;
    }
  `;

  const StyledLink = styled(Nav.Link)`
    font-size: 1.3rem;
    color: white;
    margin-right: 1rem;

    &:hover {
      color: #edcac4;
    }
  `;

  const StyledDropDown = styled(DropdownButton)`
    background-color: white;
    border-radius: 8 px;
    color: #89abe3;
    border-color: red;

    &:hover {
      color: white;
      background-color: #white;
    }
  `;

  return (
    <>
      <StyledNavbar>
        <Container>
          <StyledBrand href="#home">Online Store</StyledBrand>

          <Nav className="me-auto">
            <StyledLink href="#home">Home</StyledLink>
            <StyledLink href="#features">Sale</StyledLink>
          </Nav>

          {username === "" ? (
            <StyledButton>Login / Register</StyledButton>
          ) : (
            <StyledDropDown
              variant=""
              id="dropdown-basic-button"
              title={username}
            >
              <Dropdown.Item href="#/action-1">Cart</Dropdown.Item>
              <Dropdown.Item href="#/action-2">Settings</Dropdown.Item>
              <Dropdown.Item href="#/action-3">Log out</Dropdown.Item>
            </StyledDropDown>
          )}
        </Container>
      </StyledNavbar>
    </>
  );
}

export default Header;
