import styled from "styled-components";
import { Col, Row, Card, NavLink } from "react-bootstrap";
import axios from "axios";
import { useEffect, useState } from "react";
import StyledButton from "../components/StyledButton";

const StyledDiv = styled.div`
  margin-top: 5.5rem;
  background-color: #edcac4;
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100%;
`;

const Header = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 4rem;
  outline: 2px solid white;
  border-radius: 10px;
  background-color: #89abe3;
`;

const Title = styled.div`
  text-align: center;
  color: white;
  width: 10rem;
  font-size: 2rem;
`;

const ItemsDiv = styled.div`
  display: flex;
  flex-wrap: wrap;
  align-items: center;
  justify-content: space-evenly;
  width: 100%;
  height: 30rem;
`;

const CustomCard = styled(Card)`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: flex-end;
  width: 20rem;
  height: 30rem;
  margin-right: 5px;
  margin-left: 5px;
  margin-bottom: 2rem;
  padding-bottom: 2rem;
`;

const StyledNavLink = styled(NavLink)`
  text-decoration: none;
`;

const StyledImage = styled(Card.Img)`
  object-fit: contain;
  max-height: 18rem;
  padding: 2rem;
`;

const Body = styled(Card.Body)`
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const Text = styled(Card.Text)`
  color: #89abe3;
`;

function ForHimPage() {
  const [items, setItem] = useState([]);
  const url = "https://fakestoreapi.com/products/category/women's clothing";
  useEffect(() => {
    axios
      .get(url)
      .then((res) => {
        setItem(res.data);
      })
      .catch((err) => {
        console.error(err);
      });
  }, []);
  return (
    <StyledDiv>
      <Header>
        <Title>For Him</Title>
      </Header>
      {/* {items.map((item) => {
          return (
            <CustomCard key={item.id}>
              <StyledNavLink to={"/" + item.id}>
                <StyledImage variant="top" src={item.image} />
                <Body>
                  <Title>{item.title}</Title>
                  <Text>${item.price.toFixed(2)}</Text>
                </Body>
              </StyledNavLink>
              <StyledButton variant="secondary" text="Add to cart" />
            </CustomCard>
          );
        })} */}
    </StyledDiv>
  );
}

export default ForHimPage;
