import styled from "styled-components";
import Button from "react-bootstrap/Button";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

const CustomButton = styled(Button)`
  background-color: white;
  color: #89abe3;
  border-color: #89abe3;

  &:hover {
    background-color: #edcac4;
    border-color: #edcac4;
  }

  &:active {
    background-color: #edcac4;
  }
`;

const StyledDiv = styled.div`
  display: flex;
  margin-right: 10px;
`;

const Row = styled.div`
  display: flex;
`;

const Icon = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
`;

function StyledButton({ variant, onClick, icon, text }) {
  const iconWithText = (text, icon) => {
    return (
      <StyledDiv>
        {text}
        <Icon>
          <FontAwesomeIcon icon={icon} />
        </Icon>
      </StyledDiv>
    );
  };
  const iconWithoutText = (
    <StyledDiv>
      <Icon>
        <FontAwesomeIcon icon={icon} />
      </Icon>
    </StyledDiv>
  );

  const justText = <StyledDiv>{text}</StyledDiv>;

  if (text !== undefined && icon !== undefined) {
    // has text and icon
  } else if (icon !== undefined) {
    // has icon only
  } else if (text !== undefined) {
    // has text only
  } else {
    // has nothing

    return;
  }

  return (
    <CustomButton variant={variant} onClick={onClick}>
      <Row>
        {text && <StyledDiv>{text}</StyledDiv>}
        {icon && (
          <Icon>
            <FontAwesomeIcon icon={icon} />
          </Icon>
        )}
      </Row>
    </CustomButton>
  );
}

export default StyledButton;
