import styled from "styled-components";
import { Row, Col, Button } from "react-bootstrap";
import axios from "axios";
import { useState } from "react";
import Loader from "./Loader";

const Frame = styled.img`
  padding: 2rem;
  width: 15rem;
`;
const StyledDiv = styled.div`
  color: #89abe3;

  margin-bottom: 1rem;
  border-top: 2px solid #ccc;
`;

const Title = styled.div`
  font-size: 1.1rem;
  margin-bottom: 2rem;
  margin-top: 2rem;
`;

const Price = styled.div`
  margin-bottom: 2rem;
`;

const Quantity = styled.div`
  margin-bottom: 2rem;
`;

const Decrease = styled(Button)`
  margin-left: 1rem;
  border-top-right-radius: 0px;
  border-bottom-right-radius: 0px;
`;

const Counter = styled(Button)`
  border-top-left-radius: 0px;
  border-bottom-left-radius: 0px;
  border-top-right-radius: 0px;
  border-bottom-right-radius: 0px;
`;

const Increase = styled(Button)`
  border-top-left-radius: 0px;
  border-bottom-left-radius: 0px;
`;

const RemoveButton = styled(Button)`
  padding: 0rem;
`;

const urls = new Map();
urls.set(
  "/decrease-item-quantity",
  "http://localhost:3000/decrease-item-quantity"
);
urls.set(
  "/increase-item-quantity",
  "http://localhost:3000/increase-item-quantity"
);

function CartItem({
  id,
  image,
  title,
  price,
  quantity,
  username,
  updateParent,
}) {
  const [isLoading, setIsLoading] = useState(true);

  const handleDecrease = (isRemoving) => {
    const url = urls.get("/decrease-item-quantity");
    const body = { username: username, id: id, isRemoving: isRemoving };

    setIsLoading(true);
    axios
      .put(url, body)
      .then(async () => {
        await updateParent();
        setIsLoading(false);
      })
      .catch((err) => {
        console.error(err);
      });
  };

  const handleIncrease = () => {
    const url = urls.get("/increase-item-quantity");
    const body = { username: username, id: id };
    setIsLoading(true);

    axios
      .put(url, body)
      .then(async () => {
        await updateParent();
        setIsLoading(false);
      })
      .catch((err) => {
        console.error(err);
      });
  };

  if (isLoading) {
    return <Loader />;
  } else {
    return (
      <StyledDiv>
        <Row>
          <Col>
            <Frame src={image}></Frame>
          </Col>
          <Col>
            <Title>{title}</Title>
            <Price>${price}</Price>

            <Quantity>
              Quantity :
              <Decrease
                onClick={quantity - 1 > 0 ? () => handleDecrease(false) : null}
              >
                -
              </Decrease>
              <Counter disabled>{quantity}</Counter>
              <Increase onClick={handleIncrease}>+</Increase>
            </Quantity>

            <RemoveButton variant="link" onClick={() => handleDecrease(true)}>
              Remove
            </RemoveButton>
          </Col>
        </Row>
      </StyledDiv>
    );
  }
}

export default CartItem;
