import { Form } from "react-bootstrap";
import styled from "styled-components";

const ErrorControl = styled(Form.Control)`
  border-color: red;
`;

const ErrorText = styled(Form.Text)`
  color: red;
`;

function UserInput({ isUsernameValid, ...otherProps }) {
  if (isUsernameValid) {
    return <Form.Control {...otherProps} />;
  } else {
    return (
      <>
        <ErrorControl {...otherProps} />;
        <ErrorText>Username is empty</ErrorText>;
      </>
    );
  }
}

export default UserInput;
