import styled from "styled-components";
import { Col, Row, Card, NavLink } from "react-bootstrap";
import axios from "axios";
import { useEffect, useState } from "react";
import StyledButton from "../components/StyledButton";
import Catagory from "../components/Category";

const StyledDiv = styled.div`
  margin-top: 5.5rem;
  background-color: #edcac4;
  display: flex;
  justify-content: center;
  align-items: center;
  height: auto;
`;

const Header = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 5rem;
  outline: 2px solid white;
  border-radius: 10px;
  background-color: #89abe3;
  margin-top: 1rem;
  margin-bottom: 1rem;
`;

const Title = styled.div`
  text-align: center;
  color: white;
  width: 10rem;
  font-size: 2rem;
`;

const ItemsDiv = styled.div`
  display: flex;
  flex-wrap: wrap;
  align-items: center;
  justify-content: space-evenly;
  width: 100%;
`;
const Image = styled.img`
  object-fit: contain;
  max-height: 18rem;
  padding: 2rem;
`;

function ForHimPage() {
  const [items, setItem] = useState([]);
  const url = "https://fakestoreapi.com/products/category/women's clothing";
  useEffect(() => {
    axios
      .get(url)
      .then((res) => {
        setItem(res.data);
      })
      .catch((err) => {
        console.error(err);
      });
  }, []);
  return (
    <StyledDiv>
      <Catagory></Catagory>
    </StyledDiv>
  );
}

export default ForHimPage;
