import { useState } from "react";
import styled from "styled-components";
import Container from "react-bootstrap/Container";
import Nav from "react-bootstrap/Nav";
import Navbar from "react-bootstrap/Navbar";
import Button from "react-bootstrap/Button";
import Dropdown from "react-bootstrap/Dropdown";
import { Link } from "react-router-dom";

function Header() {
  const [username, setUsername] = useState("");

  const StyledNavbar = styled(Navbar)`
    background-color: #89abe3ff;
  `;

  const StyledButton = styled(Button)`
    background-color: #fcf6f5ff;
    color: #89abe3ff;
    border: none;
    padding: 10px 20px;
    cursor: pointer;
    transition: background-color 0.3s ease; /* Smooth transition for background color change */

    &:hover {
      background-color: #2980b9; /* Change this to your desired hover background color */
    }
  `;

  return (
    <>
      <StyledNavbar>
        <Container>
          <Navbar.Brand href="#home">Online Store</Navbar.Brand>
          <Nav className="me-auto">
            <Nav.Link href="#home">Home</Nav.Link>
            <Nav.Link href="#features">Sale</Nav.Link>
          </Nav>

          {username === "" ? (
            <StyledButton>Login / Register</StyledButton>
          ) : (
            <Dropdown>
              <Dropdown.Toggle variant="success" id="dropdown-basic">
                {username}
              </Dropdown.Toggle>

              <Dropdown.Menu>
                <Dropdown.Item href="#/action-1">Cart</Dropdown.Item>
                <Dropdown.Item href="#/action-2">Settings</Dropdown.Item>
                <Dropdown.Item href="#/action-3">Log Out</Dropdown.Item>
              </Dropdown.Menu>
            </Dropdown>
          )}
        </Container>
      </StyledNavbar>
    </>
  );
}

export default Header;
